package test.yespinoza.androidproject.Model.Utils;

import android.content.pm.PackageInfo;
import android.content.pm.PackageManager;
import android.content.pm.Signature;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.os.StrictMode;
import android.provider.Settings;
import android.util.Base64;
import android.util.Log;
import android.widget.ImageView;

import java.io.ByteArrayOutputStream;
import java.io.InputStream;
import java.net.HttpURLConnection;
import java.net.URL;
import java.security.MessageDigest;
import java.text.SimpleDateFormat;

import test.yespinoza.androidproject.Project;
import test.yespinoza.androidproject.R;

public class Helper {

    private static final String DATE_FORMAT = "dd/MM/yyyy";
    public static final String FAVORITE_ICON = "";
    public static String TextFormat(String param){
        if (param == null || param.isEmpty()) {
            return param;
        } else {
            param = param.trim();
            param = param.substring(0, 1).toUpperCase() + param.substring(1);
            char[] chars = param.toCharArray();
            for (int i = 0; i < param.length()- 2; i++)
                if (chars[i] == ' ' || chars[i] == '.' || chars[i] == ',')
                    chars[i + 1] = Character.toUpperCase(chars[i + 1]);

            return new String(chars);
        }
    }

    public static boolean dateValidation(String date){
        try{
            date = date.trim();
            String[] aux = date.split("/");
            if(aux[0].length() == 2 && aux[1].length() == 2 && aux[2].length() == 4) {
                SimpleDateFormat dateFormat = new SimpleDateFormat(DATE_FORMAT);
                dateFormat.parse(date);

                return Integer.parseInt(aux[1].toString()) <= 12 &&  Integer.parseInt(aux[0].toString()) <= 31;
            }else{
                return false;
            }
        }catch (Exception ex){
            return false;
        }
    }

    public static boolean emailValidation(String email){
        try{
            email = email.trim();
            String[] aux = email.split("@");
            String mailType = aux[1].toString();
            String[] secondPart = mailType.split("\\.");
            if(aux[0].length() >= 1
                    && secondPart[0].length() >= 1
                    && secondPart[1].length() >= 1) {
                return !email.contains(" ");
            }else{
                return false;
            }
        }catch (Exception ex){
            return false;
        }
    }

    public static Bitmap fromBase64ToBitmap(String encode){
        try {
            byte[] decodedString = Base64.decode(encode, Base64.DEFAULT);
            return BitmapFactory.decodeByteArray(decodedString, 0, decodedString.length);
        }
        catch (Exception ex){
            return null;
        }
    }

    public static Bitmap fromBase64ToBitmap_Scalad(String encode, int dstWidth, int dstHeight) {
        try {
            byte[] decodedString = Base64.decode(encode, Base64.DEFAULT);
            Bitmap bitmap = BitmapFactory.decodeByteArray(decodedString, 0, decodedString.length);
            return Bitmap.createScaledBitmap(bitmap, 96, 96, false);
        } catch (Exception ex) {
            return null;
        }
    }


    public static String fromBitmapToBase64(Bitmap bitmap){
        try{
            ByteArrayOutputStream baos = new ByteArrayOutputStream();
            bitmap.compress(Bitmap.CompressFormat.JPEG, 100, baos);
            byte[] imageBytes = baos.toByteArray();
            return Base64.encodeToString(imageBytes, Base64.DEFAULT);
        }catch(Exception ex){
            return null;
        }
    }

    public static Bitmap fromUriToBitmap(String uri){
        try {
            StrictMode.ThreadPolicy policy = new StrictMode.ThreadPolicy.Builder().permitAll().build();
            StrictMode.setThreadPolicy(policy);
            URL url = new URL(uri);
            Bitmap bitmap = BitmapFactory.decodeStream((InputStream)url.getContent());
            return bitmap;
        }catch (Exception ex) {
            return null;
        }
    }

    public static String fromUriToBase64(String uri){
        try {
            String image = fromBitmapToBase64(fromUriToBitmap(uri));
            return image;
        }catch (Exception ex) {
            return null;
        }
    }

    public static String getProjectHash(PackageManager packageManager, String packageName) {
        //Helper.getProjectHash(getPackageManager(), getPackageName());
        String Hash = "UNKNOWN";
        try {
            PackageInfo packageInfo = packageManager.getPackageInfo(packageName, PackageManager.GET_SIGNATURES);
            for (Signature signature : packageInfo.signatures) {
                MessageDigest md = MessageDigest.getInstance("SHA");
                md.update(signature.toByteArray());
                Hash = Base64.encodeToString(md.digest(), Base64.DEFAULT);
                Log.d("KeyHash:", Hash);
            }
        } catch (Exception ex) {
            Log.d("KeyHash:","Error getting Hashes");
        }
        return Hash;
    }

    public static String getDeviceId() {
        return Settings.Secure.getString(Project.getInstance().getCurrentActivity().getContentResolver(),
                Settings.Secure.ANDROID_ID);
    }
}

